svg.script:

	var xmlns="http://www.w3.org/2000/svg";
	
	window.addEventListener("load", function(){
		
		document.querySelector("#draw #ft").setAttribute("seed", Math.floor(Math.random()*1024));
		document.querySelector("#paper #papft").setAttribute("seed", Math.floor(Math.random()*1024));
		
		Digits = document.querySelector("#digits"); // get digits group
		
		num = Math.round(33+Math.random()*30); // generate random number
		bin = num.toString(2); // get it's binary form
		
		
		// make digits and signs
		
		for(var i=0; i<bin.length; i++){ // get all paths from digits
			digit(bin.charAt(i), 25+i*6, 25, 3, 10, "digit"); // get the digits of the numbers binary form
		}
		digit(2, 25+i*6, 31, 2, 5, "base", "toggle"); // append index
		
		for(var i=0; i<bin.length; i++){ // digits indexes
			digit(i, 26+(bin.length-i-1)*6, 18, 1.3, 4, "index");
		}
		// append equal sign
		digit("=1", 25+(i+1)*6, 27.5, 4, 4, "sign");
		digit("=2", 25+(i+1)*6, 27.5, 4, 4, "sign", "toggle");
		
		for(var i=0; i<bin.length; i++){ // get all paths from digits
			digit(bin.charAt(i), 1+i*6*2.5, 45, 2.5, 9, "digit");
			digit("x1", 1+i*6*2.5+4, 47.5, 3, 3, "sign");
			digit("x2", 1+i*6*2.5+4, 47.5, 3, 3, "sign");
			digit(2, 1+i*6*2.5+4+4, 45, 2.5, 9, "base");
			digit((bin.length-i-1), 1+i*6*2.5+4+4+2, 40, 2, 4, "index");
			if(i==bin.length-1){
				digit("=1", 1+i*6*2.5+4+4+3, 47.5, 3, 3, "sign");
				digit("=2", 1+i*6*2.5+4+4+3, 47.5, 3, 3, "sign", "toggle");
			}else{
				digit("+1", 1+i*6*2.5+4+4+3, 47.5, 3, 3, "sign");
				digit("+2", 1+i*6*2.5+4+4+3, 47.5, 3, 3, "sign");
			}
		}
		
		var x = 1;
		for(var i=0; i<bin.length; i++){
			digit(bin.charAt(i), x, 60, 2.5, 9, "digit");
			x += 4;
			digit("x1", x, 62.5, 3, 3, "sign");
			digit("x2", x, 62.5, 3, 3, "sign");
			x += 4;
			var d = Math.pow(2, (bin.length-i-1)).toString();
			digit(d.charAt(0), x, 60, 2.5, 9, "weight");
			if(d.length==2){
				x += 4;
				digit(d.charAt(1), x, 60, 2.5, 9, "weight");
			}
			x += 4;
			if(i==bin.length-1){
				digit("=1", x, 62.5, 3, 3, "sign");
				digit("=2", x, 62.5, 3, 3, "sign", "toggle");
			}else{
				digit("+1", x, 62.5, 3, 3, "sign");
				digit("+2", x, 62.5, 3, 3, "sign");
			}
			x += 3;
		}
		
		var x = 1, y=75;
		for(var i=0; i<bin.length; i++){
			var d = (Math.pow(2, (bin.length-i-1))*parseInt(bin.charAt(i))).toString();
			digit(d.charAt(0), x, y, 2.5, 9, "value");
			if(d.length==2){
				x += 6;
				digit(d.charAt(1), x, y, 2.5, 9, "value");
			}
			x += 6;
			if(i==bin.length-1){
				digit("=1", x, y+2.5, 3, 3, "sign");
				digit("=2", x, y+2.5, 3, 3, "sign", "toggle");
			}else{
				digit("+1", x, y+2.5, 3, 3, "sign");
				digit("+2", x, y+2.5, 3, 3, "sign");
			}
			x += 6;
		}
		
		num = num.toString();
		for(var i=0; i<num.length; i++){
			if(i==num.length-1){
				digit(num.charAt(i), x, y, 2.5, 9, "digit", "toggle");
			}else{
				digit(num.charAt(i), x, y, 2.5, 9, "digit");
			}
			x += 6;
		}
		digit(1, x-3, y+6, 1.5, 4, "base");
		digit(0, x+.5, y+6, 1.5, 4, "base");
		
		// make appear digits in serial
		
		var digits = document.querySelectorAll("#digits path");
		for(var i=0; i<digits.length-1; i++){
			digits[i].addEventListener("transitionend", function(e){
				if(e.target.getAttribute("toggle")=="true"){
					var cts = document.querySelectorAll("#comment text");
					for(var i=0; i<cts.length; i++){
						if(cts[i].classList.contains("show"))break;
					}
					var ct = cts[i];
					ct.classList.toggle("show");
					ct.classList.toggle("hide");
					var ct = cts[i+1];
					var o = e.target.nextSibling;
					ct.addEventListener("transitionend", function(){
						o.style.strokeDashoffset = 0;
					});
					ct.classList.toggle("show");
					ct.classList.toggle("hide");
				}else{
					e.target.nextSibling.style.strokeDashoffset = 0;
				}
			});
		}
		digits[0].style.strokeDashoffset = 0; // appear first digit
		
		// comments
		CGT = document.querySelector("#comment text");
		CGT.classList.toggle("show");
		CGT.classList.toggle("hide");
	});
	
	function digit(n, x, y, w, h, c, o){
		c = c || "digit";
		var d = ""; // paths d
		switch(n){ // select sign to draw
			case 1:
			case "1":
				d += " M" + (x) + "," + (y+h/3);
				d += " a" + (w*2)+","+(h/3*2) + " 0 0,0 " + (w)+","+(-h/3);
				d += " l" + (0) + "," + (h);
				break;
			case 2:
			case "2":
				d += " M" + (x) + "," + (y+w/2);
				d += " a" + (w/2)+","+(w/2) + " 0 1,1 " + (w)+","+(0);
				d += " a" + (w)+","+((h-w/2)/2) + " 0 0,1 " + (-w/2)+","+((h-w/2)/2);
				d += " a" + (w)+","+((h-w/2)/2) + " 0 0,0 " + (-w/2)+","+((h-w/2)/2);
				d += " l" + (w) + "," + (0);
				break;
			case 3:
			case "3":
				d += " M" + (x) + "," + (y+h/3);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,1 " + (w/2)+","+(h/3/2);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,1 " + (-w/2)+","+(h/3/2);
				break;
			case 4:
			case "4":
				d += " M" + (x+w/2) + "," + (y);
				d += " l" + (-w/2) + "," + (h*.7);
				d += " l" + (w) + "," + (0);
				d += " l" + (0) + "," + (-h*.3);
				d += " l" + (0) + "," + (h*.6);
				break;
			case 5:
			case "5":
				d += " M" + (x+w) + "," + (y);
				d += " h" + (-w*.85);
				d += " l" + (-w*.15)+","+(h/2);
				d += " a" + (w/2)+","+(h/3) + " 0 1,1 " + (0)+","+(h/3);
				break;
			case 6:
			case "6":
				d += " M" + (x+w) + "," + (y+h/4);
				d += " a" + (w/2)+","+(h/2) + " 0 0,0 " + (-w)+","+(h/2);
				d += " a" + (w/2)+","+(h/2) + " 0 0,0 " + (w/2)+","+(h/4);
				d += " a" + (w/2)+","+(h/2) + " 0 0,0 " + (w/2)+","+(-h/4);
				d += " a" + (w)+","+(h/4) + " 0 0,0 " + (-w)+","+(-h/4);
				break;
			case 7:
			case "7":
				d += "M" + x + "," + y;
				d += " l" + (w) + "," + (0);
				d += " a" + (w)+","+(h) + " 0 0,0 " + (-w/2)+","+(h);
				break;
			case 8:
			case "8":
				d += " M" + (x+w/2) + "," + (y);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,0 " + (0)+","+(h/2);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,1 " + (0)+","+(h/2);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,1 " + (0)+","+(-h/2);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,0 " + (0)+","+(-h/2);
				break;
			case 9:
			case "9":
				d += " M" + (x+w) + "," + (y+h/4);
				d += " a" + (w/2)+","+(h/2/2) + " 0 1,0 " + (0)+","+(+.1);
				d += " a" + (w/2)+","+(h) + " 0 0,1 " + (-w/2)+","+(h/4*3);
				d += " a" + (w/2)+","+(h/4/2) + " 0 0,1 " + (-w/2)+","+(-h/4/2);
				break;
			case "=":
				d += " M" + (x) + "," + (y+h/4);
				d += " h" + (w);
				d += " M" + (x) + "," + (y+h/4*3);
				d += " h" + (w);
				break;
			case "=1":
				d += " M" + (x) + "," + (y+h/4);
				d += " h" + (w);
				break;
			case "=2":
				d += " M" + (x) + "," + (y+h/4*3);
				d += " h" + (w);
				break;
			case "+":
				d += " M" + (x) + "," + (y+h/2);
				d += " h" + (w);
				d += " M" + (x+w/2) + "," + (y);
				d += " v" + (h);
				break;
			case "+2":
				d += " M" + (x) + "," + (y+h/2);
				d += " h" + (w);
				break;
			case "+1":
				d += " M" + (x+w/2) + "," + (y);
				d += " v" + (h);
				break;
			case "x":
			case "×":
				d += " M" + (x) + "," + (y);
				d += " l" + (w) + "," + (h);
				d += " M" + (x+w) + "," + (y);
				d += " l" + (-w) + "," + (h);
				break;
			case "x1":
			case "×1":
				d += " M" + (x) + "," + (y);
				d += " l" + (w) + "," + (h);
				break;
			case "x2":
			case "×2":
				d += " M" + (x+w) + "," + (y);
				d += " l" + (-w) + "," + (h);
				break;
			case 0:
			case "0":
			default:
				d += " M" + (x+w/2) + "," + (y);
				d += " a" + (w/2)+","+(h/2) + " 0 1,0 0.1,0";
				break;
		}
		
		var path = document.createElementNS(xmlns, "path"); // create path object
		path.setAttribute("d", d);                          // make its data
		path.setAttribute("class", c);                      // make its data
		
		if(o!==undefined)path.setAttribute("toggle", "true");
		
		var l = path.getTotalLength();
		path.style.strokeDasharray = l+.1;  // make dasharray length of whle path
		path.style.strokeDashoffset = l+.1; // and its offset too
		
		Digits.appendChild(path);                                  // append it to group
		path.getBoundingClientRect();                              // recalculate for transition
		if(c == "sign"){
			path.style.transition = "stroke-dashoffset 500ms linear";
		}else
		{
			path.style.transition = "stroke-dashoffset 1000ms linear"; // add transition (it will work after dash-offset setting)
		}
		
		return path;
	}
