svg.script:

	var xmlns = "http://www.w3.org/2000/svg";
	var g = document.querySelector("#main");
	var p = document.createElementNS(xmlns, "path");
	var d = "";
	var w = 50;
	var h = 100;
	var n = 4;
	for(var i=1; i<=n; i++){
		d += "M-50,100";
		d += "L" + (w-i*w/n) + "," + (h-i*h/n);
		d += "M50,100";
		d += "L" + -(w-i*w/n) + "," + (h-i*h/n);
	}
	p.setAttribute("d", d);
	g.appendChild(p);
